```yaml
name: cla

on:
  issue_comment:
  pull_request:
    types: [opened, closed, synchronize]

jobs:
  cla:
    runs-on: ubuntu-latest
    steps:
      - name: Get Team Members
        id: team
        uses: actions/github-script@v7a5c598405937d486b0331594b5da2b14db670da
        with:
          github-token: ${{ secrets.PERSONAL_ACCESS_TOKEN }}
          script: |
            const { context } = github;
            const { issue_number, pull_request_number } = context;
            const { owner, repo } = context.repo;
            const team = 'your-team-name';
            const teamMembers = await github.rest.teams.listMembersInOrg({
              org: owner,
              team_slug: team,
            });
            const teamMemberIds = teamMembers.data.map(member => member.id);
            const commenter = await github.rest.users.getByUsername({
              username: context.comment.user.login,
            });
            const commenterId = commenter.data.id;
            if (teamMemberIds.includes(commenterId)) {
              console.log('Commenter is a team member');
              core.setOutput('result', 'true');
            } else {
              console.log('Commenter is not a team member');
              core.setOutput('result', 'false');
            }

      - name: CLA Assistant
        if: steps.team.outputs.result == 'true'
        uses: cla-assistant/github-action@ba066dbae3769e2ce93ec8cfc4fdc51b9db628ba
        with:
          github-token: ${{ secrets.GITHUB_TOKEN }}
          repository: ${{ github.repository }}
          pull-request-number: ${{ github.event.pull_request.number }}
          team: ${{ secrets.YOUR_TEAM_SLUG }}
          signature-required: true
          signature-type: email
          signature-email: ${{ context.comment.user.email }}
          signature-comment: "Please sign the CLA to contribute to this project."
