```yaml name: Add newly created DEPR issues to the DEPR project board

on:
  issues:
    types:
      - opened

jobs:
  routeissue:
    runs-on: ubuntu-latest
    steps:
      - name: Get issue body
        id: get-body
        uses: actions/github-script@v6
        with:
          script: |
            const body = context.payload.issue.body;
            const matches = body.match(/DEPR:(.*)/i);
            if (!matches) {
              return;
            }
            const prNumber = parseInt(matches[1]);
            if (isNaN(prNumber)) {
              return;
            }
            const issueUrl = `https://github.com/rwxrob69/rwxrob69-DEPR/issues/${prNumber}`;
            console.log(`prNumber=${prNumber} ${issueUrl}`);

      - name: Add to DEPR project board
        uses: hushg25/add-workflow-to-project-board-issue@v3
        with:
          projectBoard: 'Frontend PR'
          githubToken: ${{ secrets.GITHUB_TOKEN }}
          workflowName: 'Deprecation issues'
          workflows: 'New deprecation'
          workflowSettings: '{"issues":["${{ steps.get-body.outputs.prNumber }}"],"repo":true}'
          issueURL: ${{ steps.get-body.outputs.issueUrl }}
