```yaml name: Create release branch

on:
  workflow_dispatch:
    inputs:
      base-ref:
        description: 'git ref to base from'
        required: false
        default: latest
        type: string
      bump-type:
        description: 'version bump type (patch, minor)'
        required: false
        default: patch
        type: string

permissions:
  contents: write

jobs:
  create-branch:
    permissions:
      contents: write

    runs-on: ubuntu-latest

    steps:
      - uses: actions/checkout@v3
      - uses: asdf-vm/actions/install@v1
        env:
          ASDF_DIR: ~/.asdf
          ASDF_CONFIG_ROOT: ~/.asdf
          ASDF_NODEJS_VERSION: "16.14.0"
      - uses: actions/github-script@v6
        with:
          script: |
            baseRef = env.BASE_REF
            // We are on a temporary branch with same name as latest tag
            if (baseRef === 'latest') {
              // Fetch all tags from GitHub API
              result = github.rest.repos.listTags()
              // Fetch tag data from returned objects
              tagData = result.data.map(tag => tag.name)
              // Get last one (from latest to oldest)
              baseRef = tagData.reduce((a,b) => (new Date(b)).getTime() > (new Date(a)).getTime() ? b : a)
            }

            versionBumpType = env.NEW_VERSION

            // Assume baseRef is commit sha
            // Check if branch of that commit exists
            result = github.rest.repos.getBranch({owner: github.repository.owner.login, repo: github.repository.name, branch: baseRef})
            // If branch exists, add 'bump-base' label to it (if not there already)
            if (result.data) {
              github.rest.issues.addLabels({
                owner: github.repository.owner.login,
                repo: github.repository.name,
                issue_number: result.data.id,
                labels: ['bump-base'],
              })
            }
            // Increment baseRef according to versionBumpType
            if (versionBumpType == 'patch') {
              baseRef = github.rest.repos.getCommit({
                owner: github.repository.owner.login,
                repo: github.repository.name,
                ref: baseRef,
              }).data.commit.message.replace(/(\w[\.\-\_~]*).*/g, function(g1) {
                return (parseInt(g1.split('.').pop(), 10) + 1) + '.0.0'
              })
            } else if (versionBumpType == 'minor') {
              baseRef = github.rest.repos.getCommit({
                owner: github.repository.owner.login,
                repo: github.repository.name,
                ref: baseRef,
              }).data.commit.message.replace(/(\w[\.\-\_~]*).*/g, function(g1) {
                return g1.split('.').slice(0, -1).join('.') + (parseInt(g1.split('.').pop(), 10) + 1) + '.0'
              })
            }

            // Create release branch
            result = github.rest.repos.createBranch({owner: github.repository.owner.login, repo: github.repository.name, base: baseRef, name: baseRef, ref: baseRef})
            // Set up release branch and make it readonly (if it does not already exist)
            result = github.rest.repos.edit({owner: github.repository.owner.login, repo: github.repository.name, branch: baseRef, autoPipelines: { enabled: true }, defaultBranch: true })
            result.data.protectedBranches = [{
              allowedRegexpPatterns: [],
              enableWip: false,
              pushAccessLevel: 'write',
              dismissStaleReviews: false,
              reviewDismissalRationale: ''
            }]
            result.data.protectedBranches = [{
              allowedRegexpPatterns: [],
              enableWip: false,
              pushAccessLevel: 'write',
              dismissStaleReviews: false,
              reviewDismissalRationale: ''
            }]
            result = github.rest.repos.updateProtectedBranch({owner: github.repository.owner.login, repo: github.repository.name, branch: baseRef, protection: result.data.protectedBranches})
            // Create .releasemeitup.yml
            createTag(baseRef, versionBumpType)
            // Create Bump version and add to tag
            if (versionBumpType != 'patch') {
              bumpVersion(versionBumpType)
            }
            createTag(baseRef, 'patch')
      - uses: actions/github-script@v6
        with:
          script: |
            // Update .releasemeitup.yml
            createTag(github.event.base_ref, inputs.bump-type)

            // Add version to tag
            baseRef = github.event.base_ref
